arguments.command:
cd /home/runner/work/gitclaw/gitclaw && git log --numstat --format="%ad" --date=short | awk '
/^[0-9]{4}-/ { date=$1; next }
/^[0-9]+/ { added[date]+=$1; removed[date]+=$2 }
/^-/ { added[date]+=0; removed[date]+=0 }  
END { for (d in added) print d, added[d], removed[d] }
' | sort
